using Microsoft.VisualStudio.TestTools.UnitTesting;
using System;
using System.Collections.Generic;

namespace WaterTrans.GlyphLoader.Tests
{
    [TestClass]
    public class TypefaceExample
    {
        [TestMethod]
        public void CreateSingleAdjustmentGlyphOutline()
        {
            string fontPath = System.IO.Path.Combine(Environment.CurrentDirectory, "NotoSerifJP-Regular.otf");
            Typeface tf;
            using (var fontStream = System.IO.File.OpenRead(fontPath))
            {
                // Initialize stream only
                tf = new Typeface(fontStream);
            }

            // Get halt glyph adjustment (https://docs.microsoft.com/en-us/typography/opentype/spec/features_fj#halt)
            // The user may invoke this feature in a CJKV font to get better fit for punctuation or symbol glyphs without disrupting the monospaced alignment.
            var halt = tf.GetSingleAdjustment("DFLT.DFLT.halt");

            var svg = new System.Text.StringBuilder();
            double unit = 100;
            double x = 20;
            double y = 20;
            string japaneseText = "【墨括弧】「鉤括弧」";

            svg.AppendLine("<svg width='840' height='140' viewBox='0 0 840 140' xmlns='http://www.w3.org/2000/svg' version='1.1'>");

            foreach (char c in japaneseText)
            {
                // Get glyph index
                ushort glyphIndex = tf.CharacterToGlyphMap[(int)c];

                // Get glyph outline
                var geometry = tf.GetGlyphOutline(glyphIndex, unit);

                // Get advanced width
                double advanceWidth = tf.AdvanceWidths[glyphIndex] * unit;

                // Get advanced height
                double advanceHeight = tf.AdvanceHeights[glyphIndex] * unit;

                // Get baseline
                double baseline = tf.Baseline * unit;

                // Get halt adjustment
                double xPlacement = 0;
                if (halt.ContainsKey(glyphIndex))
                {
                    var adjustment = halt[glyphIndex];
                    xPlacement = adjustment.XPlacement * unit;
                    advanceWidth += adjustment.XAdvance * unit;
                }

                // Convert to path mini-language
                string miniLanguage = geometry.Figures.ToString(x + xPlacement, y + baseline);

                svg.AppendLine($"<path d='{miniLanguage}' fill='black' stroke='black' stroke-width='0' />");
                x += advanceWidth;
            }

            svg.AppendLine("</svg>");
            System.Diagnostics.Trace.WriteLine(svg.ToString());
